import React from 'react'
import tomato from './tomato.png'
import chilli from './chili.png'
import corn from './corn.png'
import cucumber from './cucumber.png'



const Farmers = () => {

   
        const zoomImageStyle = {
          transition: 'transform 0.3s',
        };
      
        const zoomedImageStyle = {
          ...zoomImageStyle,
          transform: 'scale(1.2)',
        };
      
  return (
    <div>
       <center  style={{"margin":"200px"}}>

       <div className="image-container">
        <img src={tomato}
          
          alt="Placeholder Image"
          className="zoom-image"
          style={zoomImageStyle}
          onMouseEnter={(e) => (e.target.style.transform = 'scale(1.2)')}
          onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
        />
      </div>
       <div   style={{"transition": "transform 0.3s","hover:transform":"scale(1.0)"}}>
       <img src={tomato} className="hand" alt="pressure"/>
       </div>
       
       
       <img src={chilli} className="card-img  pr-5" alt="pressure"/>
       <img src={corn} className="card-img  pr-5" alt="pressure"/>
       <img src={cucumber} className="card-img  pr-5" alt="pressure"/>

       
      
      

       

       

   
    </center>

    
    </div>
  )
}

export default Farmers
