import React, { useState } from 'react'
import axios from 'axios'
import './App.css'

const key="a74e21e2e11290ddc996d96f101cb1c8";

const city="London";

const App = () => {

  const [city,setCity]=useState('');
  const [data,setData]=useState();




  const fetchData=async()=>{
    try{
      const response=await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${key}`);
      setData(response.data);
    }
    catch(err){
      alert('please enter city name correctly');

    }


  }





  return (
    <div className="App">
      <h2 className='Title'>weather</h2>

      <div className='input-container'>
        <input type="text"
        className='input'
        value={city}
        onChange={e=>setCity(e.target.value)}
          placeholder='enter the city name'
        />
        <button className="button" onClick={()=>fetchData()}>Fetch</button>
      </div>

      <div>
        {data && (
          <div className='container'>
            <h1 className='city-name'>{data.name},{data.sys.country}</h1>
            <div className='weather-info'>
              <div>{Math.round(data.main.temp)} C</div>
            </div>

          </div>
        )}
      </div>

    </div>
  )
}

export default App
