import React from 'react'
import tomato from './tomato.png'
import chilli from './chili.png'
import corn from './corn.png'
import cucumber from './cucumber.png'



const Farmers = () => {

   
        const zoomImageStyle = {
          transition: 'transform 0.3s',
        };
      
        const zoomedImageStyle = {
          ...zoomImageStyle,
          transform: 'scale(1.2)',
        };
      
  return (
    <div>
       <div  style={{"margin":"200px"}}>
       <div>
       WARM SEASON CROPS
       

       </div>
       <div className="container" >
        <div className="row">
       <div className='col-md-3 mt-3'>

       <div className="image-container"><b>Tomatoes - </b>
        <img src={tomato}
          
          alt="Placeholder Image"
          className="card-img  pr-5"
          style={zoomImageStyle}
          onMouseEnter={(e) => (e.target.style.transform = 'scale(1.2)')}
          onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
        />
      </div>
      <div className="imagecontainer"> Chilli -
        <img src={chilli}
          
          alt="Placeholder Image"
          className="card-img  pr-5"
          style={zoomImageStyle}
          onMouseEnter={(e) => (e.target.style.transform = 'scale(1.2)')}
          onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
        />
      </div>
      <div className="image-container"> Corn - 
        <img src={corn}
          
          alt="Placeholder Image"
          className="card-img  pr-5"
          style={zoomImageStyle}
          onMouseEnter={(e) => (e.target.style.transform = 'scale(1.2)')}
          onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
        />
      </div>
      <div className="image-container"> cucumber - 
        <img src={cucumber}
          
          alt="Placeholder Image"
          className="card-img  pr-5"
          style={zoomImageStyle}
          onMouseEnter={(e) => (e.target.style.transform = 'scale(1.2)')}
          onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
        />
      </div>

      

       </div>
       <div className='col-md-9 mt-3'>

       Summer is typically warmer with longer daylight hours.
       Adequate irrigation is crucial during the hot and dry summer months.
       </div>

       </div>
      </div>


      
       
       
       
       

       
      
      

       

       

   
    </div>

    
    </div>
  )
}

export default Farmers
