import React, { useRef } from "react";

import './Logi.css';
import profile from "./user.png";
import App from "./App";

import pass from "./pass.png";
import email_logo from "./email_logo.jpg";

function Adduser(){
    const email=useRef()
    const password=useRef()
    const getEmail=localStorage.getItem("emailData")
    const getPassword=localStorage.getItem("passwordData")
    const handleSubmit=()=>{
        if(email.current.value==="guna@gmail.com"&&password.current.value==="guna@34"){
            localStorage.setItem("emailData","guna@gmail.com")
            localStorage.setItem("passwordData","guna@34")
        }
        
      
    }
   
    return(
      <center>

        <div>
            {
                getEmail&&getPassword?
                <App/>:
                <div className="box">
          <div className="main">
            <form onSubmit={handleSubmit}>

              <h2 > LOGIN HERE</h2>
              <div className="wlc">
              <br/>
                
                <img src={profile} alt="profile" className="border"/>
               
                
                    <input type="text" name="username" placeholder="username" className="name"/><br/>
                </div>
          
            
                <div className="second-input" >
                <br/>
               
                
                <img src={email_logo} alt="email" className="border"/>
                    <input type="text" ref={email} placeholder="abc@gmail.com" className="name" /><br/>
                </div>
          
                <div className="second-input"  >
               <br/>
                <img src={pass} alt="pass" className="border"/>
                    <input type="password" ref={password} placeholder="password" className="name"/><br/>
                </div>
                <div className="second-input"  >
                <button>Login</button>
                </div>
                
                
            </form>
            </div>
                </div>
            }
            
        </div>
        </center>
    );
}
export default Adduser;
