import React, { useState } from 'react'
import axios from 'axios'
import './App.css'
import humidity from './humidity.png'
import pressure from './resilient.png'

const key="a74e21e2e11290ddc996d96f101cb1c8";

const city="London";

const App = () => {

  const [city,setCity]=useState('');
  const [data,setData]=useState();




  const fetchData=async()=>{
    try{
      const response=await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${key}`);
      setData(response.data);
      console.log(response.data);
    }
    catch(err){
      alert('please enter city name correctly');

    }


  }





  return (
    <div className="App">
      <h2 className='Title'>weather</h2>

      <div className='input-container'>
        <input type="text"
        className='input'
        value={city}
        onChange={e=>setCity(e.target.value)}
          placeholder='enter the city name'
        />
        <button className="button" onClick={()=>fetchData()}>Fetch</button>
      </div>

      <div>
        {data && (
          <div className='container'>
            <h1 className='city-name'>{data.name},{data.sys.country}</h1>
            <div className='weather-info'>
              <div>{Math.round(data.main.temp- 273.15)} Â°C</div>
              <div>{data.weather[0].description}</div>
              <div className='coordinate'>
                 <div>Lat -{data.coord.lat}</div>
                 <div>Lon -{data.coord.lon}</div>


              </div>



            </div>

          </div>
        )}
      </div>

      <div className='card'>
          <div >
            <h1>Humidity {data.main.humidity} %</h1>
          </div>
      </div>
      <div className='card'>
          <div >
            <h1>Pressure {data.main.pressure} mb</h1>
          </div>
      </div>

                   <div class="col-md-2 mt-3"> 
                      <div className='cardcolor'>
                          <div class="card bg-primary bg-gradient text-light">

                              <div class="card-body text-center">
                                <img src={pressure} class="card-img" alt=""/>Pressure
                                <h5 class="card-title mt-3"> {data.main.pressure} mb</h5>


                              </div>
                          </div>
                      </div>
                    </div> 

    </div>
  )
}

export default App
